import React from 'react';
import '../styles/Style.css';
import { Link } from '@mui/material';
import { Feed } from '@mui/icons-material';
import AboutImage from '../images/image/AboutImage.png';

const About = () => {
  return (

    <div id='about' className='about dark-mode'>
      <img className='about-img' src={AboutImage} />
      <div className='about-text'>
        <h4 style={{ color: "blue", fontWeight: "800" }}>ABOUT ME</h4>
        <h2 style={{ fontWeight: "800" }}>A dedicated Front-end Developer
          based in Mumbai üìç</h2>
        <p className='hero-text' style={{ fontWeight: "500", color: "gray" }}>I am a passionate and driven individual with a strong interest in web development and building dynamic and engaging user interfaces using the React JavaScript Library. I have completed relevant coursework or have prior experience in web development and programming languages such as HTML, CSS, and JavaScript.
          <br />
          <br />
          I am gaining valuable hands-on experience in React development, working on real-world projects, and learning from experienced developers. I am eager to continue developing my skills and building my career as a React Developer.</p>
        <div>
          <a href='https://www.dropbox.com/s/1rbwspv4lejnak4/Siddharth%27s%20Resume%20%2810%29.pdf?dl=0' className='social'><Feed style={{ fontSize: "35px" }} /><span style={{ fontWeight: "800", margin: "0 5px" }}>View Resume</span></a>
          {/* <a href='#'>View Resume</a> */}
        </div>
      </div>
    </div>
  )
}

export default About
