import React from 'react';
import '../styles/Style.css';
import { Link } from '@mui/material';

const About = () => {
  return (
    
    <div className='about'>
      <img height={350} width={500} style={{ marginTop: "7rem" }} src='https://media2.giphy.com/media/v1.Y2lkPTc5MGI3NjExcWowNXYyczc0cnZ6MmM4enZrNGJxNmhuemFpejV2NGU5ZDA2NGtibiZlcD12MV9pbnRlcm5hbF9naWZfYnlfaWQmY3Q9Zw/WtTnAfZn6aVJfBzlN3/giphy.gif' />
      <div className='about-text' style={{ margin: "10rem" }}>
        <h4 style={{ color: "blue", fontWeight: "800" }}>ABOUT ME</h4>
        <h2 style={{ fontWeight: "800" }}>A dedicated Front-end Developer
          based in Mumbai üìç</h2>
        <p style={{fontSize:"20px"}}>I am a passionate software engineer intern and driven individual with a strong interest in web development and building dynamic and engaging user interfaces using the React JavaScript library. I have completed relevant coursework or have prior experience in web development and programming languages such as HTML, CSS, and JavaScript.
          I am gaining valuable hands-on experience in React development, working on real-world projects, and learning from experienced developers. I am eager to continue developing my skills and building my career as a React Developer.</p>
      </div>
    </div>
  )
}

export default About
